func levelBadgeClass(level string) string {
	switch level {
	case "ERROR", "error", "ERR":
		return "bg-danger"
	case "WARN", "warn", "WARNING", "warning":
		return "bg-warning text-dark"
	case "INFO", "info":
		return "bg-info text-dark"
	case "DEBUG", "debug":
		return "bg-secondary"
	default:
		return "bg-light text-dark"
	}
}
